contextMenus.contextMenu:

	try { window.SaveCommand(); } catch(e) { alert('No se pueden guardar datos: \n\n'+e.description) } 


	try { window.toClipboard(); } catch(e) { alert(e.description) }


	try { window.PrintCommand(); } catch(e) { alert(e.description) }


	try { oWindow=(window.parent.WorkArea || window.parent.resultados || window.parent); oWindow.goBack(); } catch(e) { alert(e.description) }


	try { oWindow=(window.parent.WorkArea || window.parent.resultados || window.parent); oWindow.refreshPage(); } catch(e) { alert(e.description) } 


	try { oWindow=(window.parent.WorkArea || window.parent.resultados || window.parent); oWindow.location.href=(updateURLString(oWindow.location.href, 'debug', 'true')); } catch(e) { alert(e.description) } 


	try { oWindow=(window.parent.WorkArea || window.parent.resultados || window.parent); toggleLanguage(); document.execCommand('Refresh'); } catch(e) { alert(e.description) } 

top.frames.location.reload();
